import { ToastContainer } from 'react-toastify';
import isAuth from './IsAuth';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCookies } from 'react-cookie';
import New from './New';
import Body from './Body';
const Home = () => {
  const navigate = useNavigate();
  const [cookies, setCookie, removeCookie] = useCookies(['token']);
  const tokenValue = cookies.token;

  useEffect(() => {
    setCookie('myCookie', tokenValue, { path: '/' });
  }, [setCookie, tokenValue]);
  const cookieValue = cookies.myCookie;

  const { authenticated, username } = isAuth();
  console.log(username);
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const getUserDetails = async () => {
      console.log(username);
      const response = await fetch(
        `http://localhost:4000/api/user/${username}`
      );

      const user = await response.json();
      console.log(user);

      setUserDetails(user);
    };

    getUserDetails();
  }, [username]);

  if (!authenticated) {
    navigate('/login');
    return null;
  }

  const logout = () => {
    removeCookie('token');
    navigate('/login');
  };

  return (
    <>
      <div className="home_page">
        {userDetails ? (
          <>
            {/* <h4>
              {' '}
              Welcome <span>{userDetails.email}</span>
            </h4>
            <button onClick={logout}>LOGOUT</button> */}
            <New />
            <Body />
          </>
        ) : (
          <p>Loading user details...</p>
        )}
        <ToastContainer />
      </div>
    </>
  );
};

export default Home;
