import React from 'react';
import '../css/productpage.css';
import HomeHeader from '../subpages/HomeHeader';
import sonyWirelessHeadphones from '../images/sonywirelessheadphones.jpeg';
import data from '../data';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
const ProductPage = () => {
  const { slug } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/product/${slug}`); // Replace with your API endpoint
        if (response.ok) {
          const data = await response.json();
          setProduct(data);
        }
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [slug]);
  return (
    <div className="product-container">
      <HomeHeader />
      <div className="product-body">
        <div className="product-left">
          <img
            src={sonyWirelessHeadphones}
            alt="Headphone"
            className="product-image"
          />
          <input
            type="button"
            placeholder="Share"
            className="product-share"
            value="Share"
          />
        </div>
        <div className="product-right">
          <div className="product-description"></div>
          <h1>Product Name</h1>
          <h3>Brand</h3>
          <h3>Rating</h3>
          <h3>Price: $1799</h3>
          <div className="product-buy">
            <input
              type="button"
              className="product-add-to-cart"
              value="Add to Cart"
            />
            <input type="button" className="product-buy-now" value="Buy Now" />
          </div>

          <span className="product-label">Brand Name:</span>
          <span className="product-value">Samsung</span>
          <br />
          <span className="product-label">Model Name:</span>
          <span className="product-value">Samsung</span>
          <br />
          <span className="product-label">Color:</span>
          <span className="product-value">Samsung</span>
          <br />
          <span className="product-label">Description:</span>
          <span className="product-value">Samsung</span>
          <br />
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
